INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (414, 'Programa Nacional de Dragagem Portuária e Hidroviária', '2007-01-01', '393/2007', '20 de setembro de 2007', '11610/2007', '2007-12-13', '2013-06-05', '§ 1o  O Programa de que trata o caput deste artigo abrange as obras e serviços de engenharia de dragagem do leito das vias aquaviárias, compreendendo a remoção do material sedimentar submerso e a escavação ou derrocamento do leito, com vistas à manutenção da profundidade dos portos em operação ou na sua ampliação.', '#N/A', 'Programa sem ação orçamentária associada', 'Lei ordinária', 5, 4, 13, null, null, '§ 1o  O Programa de que trata o caput deste artigo abrange as obras e serviços de engenharia de dragagem do leito das vias aquaviárias, compreendendo a remoção do material sedimentar submerso e a escavação ou derrocamento do leito, com vistas à manutenção da profundidade dos portos em operação ou na sua ampliação.', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (419, 'Programa Nacional de Inclusão de Jovens (ProJovem)', '2005-01-01', '238/2005', '02 de fevereiro de 2005', '11129/2005', '2005-07-01', null, 'Art. 1º Fica instituído, no âmbito da Secretaria-Geral da Presidência da República, o Programa Nacional de Inclusão de Jovens – ProJovem, programa emergencial e experimental, destinado a executar ações integradas que propiciem aos jovens brasileiros, na forma de curso previsto no art. 81 da Lei nº 9.394, de 20 de dezembro de 1996, elevação do grau de escolaridade visando a conclusão do ensino fundamental, qualificação profissional voltada a estimular a inserção produtiva cidadã e o desenvolvimento de ações comunitárias com práticas de solidariedade, exercício da cidadania e intervenção na realidade local.             (Revogado pela Medida Provisória nº 411, de 2007).             (Revogado pela Lei nº 11.692, de 2008)
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (428, 'Programa Rota 2030', '2018-01-01', '843/2018', '06 de julho de 2018', '13755/2018', '2018-12-10', '2018-11-20', 'Art. 7º Fica instituído o Programa Rota 2030 - Mobilidade e Logística, com o objetivo de apoiar o desenvolvimento tecnológico, a competitividade, a inovação, a segurança veicular, a proteção ao meio ambiente, a eficiência energética e a qualidade de automóveis, de caminhões, de ônibus, de chassis com motor e de autopeças. (Produção de efeito)', '#N/A', 'Iniciativa Tributária', 'Lei ordinária', 5, 4, 13, null, null, 'Art. 9º Poderão habilitar-se ao Programa Rota 2030 - Mobilidade e Logística as empresas que: (Produção de efeito)<br />I - produzam, no País, os veículos classificados nos códigos 87.01 a 87.06 da Tipi, aprovada pelo Decreto nº 8.950, de 29 de dezembro de 2016 , as autopeças ou os sistemas estratégicos para a produção dos veículos classificados nos referidos códigos da Tipi, conforme regulamento do Poder Executivo federal; ou<br />II - tenham projeto de desenvolvimento e produção tecnológica aprovado para a produção, no País, de novos produtos ou de novos modelos de produtos já existentes referidos no inciso I do caput deste artigo, ou de novas soluções estratégicas para a mobilidade e logística, conforme regulamento do Poder Executivo federal.', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (448, 'Política de Ciência, Tecnologia e Inovação em Saúde (PNCTIS)', '2004-01-01', 'Não se aplica', 'Não se aplica', 'Não se aplica', '2004-07-28', null, 'Contribuir para que o desenvolvimento nacional se faça de modo sustentável, e com apoio na produção de conhecimentos técnicos e científicos ajustados às necessidades econômicas, sociais, culturais e políticas do País.', 'A política não foi criada por um instrumento legal. Foi aprovada durante a 2ª Conferência Nacional de Ciência, Tecnologia e Inovação em Saúde e é mencionada na Portaria de Consolidação nº 1, de 28 de setembro de 2017.', 'Política sem ação orçamentária associada', 'Não se aplica', 4, 1, 1, null, null, 'Sem especificação', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (492, 'Programa Nacional de Vigilância da Qualidade da Água para Consumo Humano (VIGIAGUA)', '2005-01-01', 'Não se aplica', 'Não se aplica', 'Não se aplica', null, null, 'Desenvolver ações de vigilância em saúde ambiental relacionada à qualidade da água para consumo humano que garantam à população o acesso à água em quantidade sufi ciente e qualidade compatível com o padrão de potabilidade estabelecido na legislação vigente, para a promoção da saúde.', 'Este documento trata do Programa Nacional de Vigilância em Saúde Ambiental relacionada à qualidade da água para consumo humano, a ser implementado no Brasil. Contém as diretrizes e princípios norteadores do SUS, o campo e forma de atuação e as principais atividades necessárias para a sua execução (https://bvsms.saude.gov.br/bvs/publicacoes/programa_agua_consumo_humano.pdf). .https://bvsms.saude.gov.br/bvs/saudelegis/gm/2021/prt0888_24_05_2021_rep.html; https://bvsms.saude.gov.br/bvs/saudelegis/gm/2011/prt2914_12_12_2011.html; http://189.28.128.100/dab/docs/legislacao/portaria518_25_03_04.pdf', '#N/A', 'Não se aplica', 5, 1, 1, null, null, 'População em geral ', null);
