INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (535, 'Política Nacional de Educação Popular em Saúde (PNEPS/SUS)', '2013-01-01', 'Não se aplica', 'Não se aplica', '2761/2013', '2013-11-19', null, 'Art. 5º A PNEPS-SUS tem como objetivo geral implementar a Educação Popular em Saúde no âmbito do SUS, contribuindo com a participação popular, com a gestão participativa, com o controle social, o cuidado, a formação e as práticas educativas em saúde.
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (622, 'Programa Nacional de Prevenção e Controle dos Distúrbios por Deficiência de Iodo (DDI)', '2005-01-01', 'Não se aplica', 'Não se aplica', '2362/2005', '2005-12-01', null, 'Art. 2º  Definir que o Pró-Iodo destina-se a promover a eliminação virtual sustentável dos DDI mediante a obrigatoriedade de iodação do sal destinado ao consumo humano em todo o território nacional.', 'A portaria https://bvsms.saude.gov.br/bvs/saudelegis/gm/2005/prt2362_01_12_2005.html fala em reestruturação do Programa, mas este manual https://bvsms.saude.gov.br/bvs/publicacoes/manual_tecnico_operacional_proiodo.pdf fala em criação. A portaria revoga outra de 1994 (Art. 18  Fica revogada a Portaria nº 2.165/GM, de 29 de dezembro de 1994, publicada no Diário Oficial nº 1, de 2 de janeiro de 1995, Seção 1, página 45), mas não consegui encontrá-la. Portanto, o Programa pode ter começado no ano de 1994.', '#N/A', 'Portaria', 5, 1, 1, null, null, 'Toda a população', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (542, 'Política Nacional de Medicamentos', '1998-01-01', 'Não se aplica', 'Não se aplica', '3916/1998', '1998-10-30', null, 'Art. 1º Aprovar a Política Nacional de Medicamentos, cuja íntegra consta do anexo desta Portaria. O seu propósito precípuo é o de garantir a necessária segurança, eficácia e qualidade dos medicamentos, a promoção do uso racional e o acesso da população àqueles considerados essenciais. A Política Nacional de Medicamentos, como parte essencial da Política Nacional de Saúde, constitui um dos elementos fundamentais para a efetiva implementação de ações capazes de promover a melhoria das condições da assistência à saúde da população. A Lei n.º 8.080/90, em seu artigo 6o, estabelece como campo de atuação do Sistema Único de Saúde - SUS - a "formulação da política de medicamentos (...) de interesse para a saúde (...)".
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (544, 'Política Nacional de Promoção à Saúde', '2006-01-01', 'Não se aplica', 'Não se aplica', ' 687/2006', '2006-03-30', null, 'Promover a qualidade de vida e reduzir vulnerabilidade e riscos à saúde relacionados aos seus determinantes e condicionantes – modos de viver, condições de trabalho, habitação, ambiente, educação, lazer, cultura, acesso a bens e serviços essenciais.', '#N/A', 'Política com ação orçamentária associada', 'Portaria', 4, 1, 1, null, null, 'Sem especificação  População em geral', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (546, 'Política Nacional de Regulação do Sistema Único de Saúde', '2008-01-01', 'Não se aplica', 'Não se aplica', '1559/2008', '2008-08-01', null, 'Sem especificação', '#N/A', 'Política com ação orçamentária associada', 'Portaria', 4, 1, 1, null, null, 'Art. 19.  O processo de seleção de indivíduos e famílias candidatos a beneficiários do Programa Nacional de Reforma Agrária será realizado por projeto de assentamento, observada a seguinte ordem de preferência na distribuição de lotes: I - ao desapropriado, ficando-lhe assegurada a preferência para a parcela na qual se situe a sede do imóvel, hipótese em que esta será excluída da indenização devida pela desapropriação; II - aos que trabalham no imóvel desapropriado como posseiros, assalariados, parceiros ou arrendatários, identificados na vistoria; III - aos trabalhadores rurais desintrusados de outras áreas, em virtude de demarcação de terra indígena, criação de unidades de conservação, titulação de comunidade quilombola ou de outras ações de interesse público; IV - ao trabalhador rural em situação de vulnerabilidade social que não se enquadre nas hipóteses previstas nos incisos I, II e III deste artigo; V - ao trabalhador rural vítima de trabalho em condição análoga à de escravo; VI - aos que trabalham como posseiros, assalariados, parceiros ou arrendatários em outros imóveis rurais; VII - aos ocupantes de áreas inferiores à fração mínima de parcelamento (Redação dada pela Lei nº 13.465, de 2017).', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (549, 'Política Nacional de Saúde da Pessoa Idosa (PNPI)', '2006-01-01', 'Não se aplica', 'Não se aplica', '2528/2006', '2006-10-19', null, 'Recuperar, manter e promover a autonomia e a independência dos indivíduos idosos, direcionando medidas coletivas e individuais de saúde para esse fim, em consonância com os princípios e diretrizes do Sistema Único de Saúde. É alvo dessa política todo cidadão e cidadã brasileiros com 60 anos ou mais de idade.', 'Objetivo como descrito na versão mais recente da política', '#N/A', 'Portaria', 4, 1, 1, null, null, 'Art. 2º A Política Nacional de Saneamento abrangerá:<br />a) saneamento básico, compreendendo abastecimento de água, sua fluoretação e destinação de dejetos; b) esgotos pluviais e drenagem;<br />c) contrôle da poluição ambiental, inclusive do lixo; d) contrôle das modificações artificiais das massas de água; e) contrôle de inundações e de erosões.', null);
