INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (628, 'Programa Nacional de Suplementação de Ferro (PNSF)', '2014-01-01', 'Não se aplica', 'Não se aplica', '1977/2014', '2014-09-12', null, 'Art. 2º O PNSF tem como objetivo prevenir e controlar a anemia por deficiência de ferro e deverá ser implantado nas Unidades Básicas de Saúde (UBS) de todos os Municípios brasileiros.', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2014/prt1977_12_09_2014.html; https://bvsms.saude.gov.br/bvs/saudelegis/gm/2005/prt0730_13_05_2005.html', '#N/A', 'Portaria', 5, 1, 1, null, null, 'Art. 2º Parágrafo único. O público a ser atendido serão as crianças entre 6 (seis) e 24 (vinte e quatro) meses de idade, gestantes e mulheres até o 3º mês pós-parto e pós-aborto, que deverão ser suplementadas de forma profilática e universal, observando-se o Manual de Condutas Gerais do Programa Nacional de Suplementação de Ferro, definido pela Coordenação-Geral de Alimentação e Nutrição (CGAN/DAB/SAS/MS), disponível no sítio eletrônico http://dab.saude. gov. br/ portaldab/ biblioteca. php? conteudo= publicacoes/ manual_ f erro2013.', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (559, 'Programa Bioeconomia Brasil (Sociobiodiversidade)', '2019-01-01', 'Não se aplica', 'Não se aplica', '121/2019', '2019-06-18', null, 'Art. 1º Fica instituído, no âmbito do Ministério da Agricultura, Pecuária e Abastecimento - MAPA, o Programa Bioeconomia Brasil - Sociobiodiversidade.
