INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (565, 'Programa de Assistência Ventilatória Não Invasiva aos Portadores de Doenças Neuromusculares', '2008-01-01', 'Não se aplica', 'Não se aplica', '1370/2008', '2001-09-04', null, 'Art. 1º § 1º O Programa ora instituído tem por objetivo melhorar a atenção à saúde dos portadores de doenças neuromusculares, adotar medidas que permitam retardar a perda da função vital destes pacientes ou mesmo evitá-la, promover a melhoria da sua qualidade e expectativa de vida e, ainda, ampliar o acesso à ventilação nasal intermitente de pressão positiva quando a mesma estiver indicada.', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2008/prt1370_03_07_2008.html; https://bvsms.saude.gov.br/bvs/saudelegis/gm/2001/prt1531_04_09_2001.html', '#N/A', 'Portaria', 5, 1, 1, null, null, 'Portadores de Doenças Neuromusculares', null);
