INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (593, 'Programa de Treinamento em Epidemiologia Aplicada aos Serviços do Sistema Único de Saúde (EpiSUS)', '2018-01-01', 'Não se aplica', 'Não se aplica', '1430/2018', '2018-06-11', null, 'Art. 335-B. O Programa EpiSUS tem como objetivo geral aprimorar a capacidade técnica de profissionais de nível superior, por meio de treinamento em serviço para atuar frente às investigações epidemiológicas, tais como surtos e emergências em saúde pública no âmbito dos serviços do Sistema Único de Saúde. Art. 335-C. São objetivos específicos do Programa EpiSUS: I - capacitar profissionais de nível superior em epidemiologia de campo e vigilância em saúde; II - desenvolver capacidade técnica-científica para colaborar na resolução de problemas de saúde pública; III - colaborar na resposta às emergências em saúde pública, principalmente em investigações de surtos; IV - contribuir para a difusão do conhecimento técnico-científico, por meio da comunicação científica escrita e oral, da divulgação de resultados de investigações de surtos e de outros estudos desenvolvidos durante o treinamento; V - colaborar no planejamento e condução de investigações epidemiológicas de surtos/epidemias e outros eventos de saúde pública, incluindo a coleta, a análise, a descrição e a interpretação de dados para orientar a rápida tomada de decisão; VI - direcionar as ações de prevenção e controle de eventos de importância em saúde pública; VII - colaborar e conduzir avaliações de sistemas de vigilância em saúde ou programas específicos de saúde pública e análise de dados gerados pelos sistemas de informação; VIII - conduzir pesquisas direcionadas às necessidades das áreas de vigilância em saúde, desenvolvendo estudos de caráter técnico-científico; e IX - colaborar no planejamento e execução de estudos epidemiológicos, incluindo a coleta, a análise, a descrição e a interpretação de dados para orientar a tomada de decisão, de forma rápida.', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2018/prt1430_12_06_2018.html', '#N/A', 'Portaria', 5, 1, 1, null, null, 'Art. 335-B. O Programa EpiSUS tem como objetivo geral aprimorar a capacidade técnica de profissionais de nível superior, por meio de treinamento em serviço para atuar frente às investigações epidemiológicas, tais como surtos e emergências em saúde pública no âmbito dos serviços do Sistema Único de Saúde.', null);
