INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (611, 'Programa Nacional de Competitividade em Vacinas (INOVACINA)', '2006-01-01', 'Não se aplica', 'Não se aplica', '972/2006', '2006-05-03', null, 'Art. 2º  O Programa INOVACINA tem por finalidade criar condições para alcançar a auto-suficiência nacional na produção das vacinas incluídas no Programa Nacional de Imunização (PNI), por meio das seguintes ações: I - investir na infra-estrutura e no custeio da pesquisa e desenvolvimento em vacinas, incluindo bolsas de pesquisa; II - investir no estabelecimento de condições de Boas Práticas de Produção (BPP) nos laboratórios públicos e privados sem fins lucrativos, produtores de vacinas no País; III - aperfeiçoar o sistema de regulação de vacinas, com especial atenção aos mecanismos desenvolvidos pela Agência Nacional de Vigilância Sanitária (ANVISA); e IV - investir no estabelecimento de uma Rede Pública de Ensaios pré-clínicos e clínicos de vacinas, destinada a avaliar a segurança, a eficácia e efetividade das vacinas produzidas. ', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2006/prt0972_03_05_2006.html', '#N/A', 'Portaria', 5, 1, 1, null, null, 'Art. 2º  O Programa INOVACINA tem por finalidade criar condições para alcançar a auto-suficiência nacional na produção das vacinas incluídas no Programa Nacional de Imunização (PNI), por meio das seguintes ações: I - investir na infra-estrutura e no custeio da pesquisa e desenvolvimento em vacinas, incluindo bolsas de pesquisa; II - investir no estabelecimento de condições de Boas Práticas de Produção (BPP) nos laboratórios públicos e privados sem fins lucrativos, produtores de vacinas no País; III - aperfeiçoar o sistema de regulação de vacinas, com especial atenção aos mecanismos desenvolvidos pela Agência Nacional de Vigilância Sanitária (ANVISA; e IV - investir no estabelecimento de uma Rede Pública de Ensaios pré-clínicos e clínicos de vacinas, destinada a avaliar a segurança, a eficácia e efetividade das vacinas produzidas. ', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (617, 'Programa Nacional de Genômica e Saúde de Precisão (Genomas Brasil)', '2020-01-01', 'Não se aplica', 'Não se aplica', '1949/2020', '2020-08-04', null, 'Art. 837-L. Fica instituído, no âmbito do Sistema Único de Saúde (SUS), o Programa Nacional de Genômica e Saúde de Precisão (Genomas Brasil). Parágrafo único. O Genomas Brasil é um programa de ciência, tecnologia e inovação que tem como finalidade: I - incentivar o desenvolvimento científico e tecnológico nacional nas áreas de genômica e saúde de precisão; II - promover o desenvolvimento da indústria genômica nacional; e III - estabelecer prova de conceito para uma linha de cuidado em genômica e saúde de precisão no âmbito do SUS. Art. 837- N. São objetivos do Programa Genomas Brasil: I - estabelecer o genoma de referência da população brasileira; II - instituir um banco nacional de dados genômicos e clínicos; III - promover o aumento da capacidade científica instalada e do capital intelectual nacional em medicina genômica e saúde de precisão; IV - promover o fortalecimento e a competitividade da indústria nacional de insumos e de produtos de medicina genômica e saúde de precisão; e V - capacitar a força de trabalho do SUS em medicina genômica e saúde de precisão. ', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2020/prt1949_05_08_2020.html', 'Indefinida', 'Portaria', 5, 1, 1, null, null, 'I - promover o desenvolvimento da indústria genômica nacional', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (627, 'Programa Nacional de Segurança do Paciente', '2013-01-01', 'Não se aplica', 'Não se aplica', '529/2013', '2013-04-01', null, 'Art. 2º O PNSP tem por objetivo geral contribuir para a qualificação do cuidado em saúde em todos os estabelecimentos de saúde do território nacional. Art. 3º Constituem-se objetivos específicos do PNSP: I - promover e apoiar a implementação de iniciativas voltadas à segurança do paciente em diferentes áreas da atenção, organização e gestão de serviços de saúde, por meio da implantação da gestão de risco e de Núcleos de Segurança do Paciente nos estabelecimentos de saúde; II - envolver os pacientes e familiares nas ações de segurança do paciente; III - ampliar o acesso da sociedade às informações relativasà segurança do paciente; IV - produzir, sistematizar e difundir conhecimentos sobre segurança do paciente; e V - fomentar a inclusão do tema segurança do paciente no ensino técnico e de graduação e pós-graduação na área da saúde. ', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2013/prt0529_01_04_2013.html', '#N/A', 'Portaria', 5, 1, 1, null, null, 'Usuários de serviços de saúde públicos e privados', null);
