INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (605, 'Programa Nacional de Avaliação de Serviços de Saúde (PNASS)', '2015-01-01', 'Não se aplica', 'Não se aplica', '28/2015', '2005-03-10', null, 'Art. 2º O PNASS tem como objetivo geral avaliar a eficiência, eficácia e efetividade das estruturas, processos e resultados relacionados ao risco, acesso e satisfação dos cidadãos frente aos serviços de saúde do Sistema Único de Saúde (SUS), buscando a apreensão mais completa e abrangente possível da suas realidades, em suas diferentes dimensões, na busca da resolubilidade e qualidade. Art. 3º O PNASS tem como objetivos específicos: I - incentivar a cultura avaliativa dos gestores para os serviços de saúde; II - fomentar a cultura avaliativa nos estabelecimentos de saúde; III - ser instrumento de apoio à gestão do SUS; IV - produzir conhecimento qualitativo da rede de serviços de saúde; V - implementar padrões de conformidade dos serviços de saúde; VI - incorporar indicadores de produção para avaliação de serviços de saúde; VII - aferir a satisfação dos usuários do SUS; VIII - conhecer as condições e relações de trabalho dos profissionais nos estabelecimentos de saúde; IX - identificar oportunidades e possibilidades de melhoria; X - possibilitar a observação de experiências exitosas para melhoria da qualidade local; e XI - disponibilizar os resultados para conhecimento público. ', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2015/prt0028_08_01_2015.html', 'Programa com ação orçamentária associada', 'Portaria', 5, 1, 1, null, null, 'Art. 5º O PNASS será aplicado periodicamente nos estabelecimentos de atenção especializada em saúde, ambulatorial e hospitalar, contemplados com recursos financeiros provenientes de programas, políticas e incentivos do Ministério da Saúde. § 1º Os estabelecimentos de atenção especializada em saúde a serem avaliados pelo PNASS serão indicados a partir de critérios estabelecidos pelo Ministério da Saúde.', null);
